'use strict';

var _ = require('lodash');

var createExporter = require('../exporter');

var template = "#!/bin/bash \n\n# Save this script into set_colors.sh, make this file executable and run it: \n# \n# $ chmod +x set_colors.sh \n# $ ./set_colors.sh \n# \n# Alternatively copy lines below directly into your shell. \n\ngconftool-2 -s -t string /apps/guake/style/background/color '{{=c.background}}'\ngconftool-2 -s -t string /apps/guake/style/font/color '{{=c.foreground}}'\ngconftool-2 -s -t string /apps/guake/style/font/palette '{{=c[0]}}:{{=c[1]}}:{{=c[2]}}:{{=c[3]}}:{{=c[4]}}:{{=c[5]}}:{{=c[6]}}:{{=c[7]}}:{{=c[8]}}:{{=c[9]}}:{{=c[10]}}:{{=c[11]}}:{{=c[12]}}:{{=c[13]}}:{{=c[14]}}:{{=c[15]}}'\n";

module.exports = {
  export: createExporter(template, _.partialRight(_.mapValues, function (color) {
    return color.toDoubleHex();
  }))
};
