'use strict';

var _ = require('lodash');

var createExporter = require('../exporter');

var template = "# --- special colors ---\n\n[Background]\nColor={{=c.background}}\n\n[BackgroundIntense]\nColor={{=c.background}}\n\n[Foreground]\nColor={{=c.foreground}}\n\n[ForegroundIntense]\nColor={{=c.foreground}}\nBold=true\n\n# --- standard colors ---\n\n[Color0]\nColor={{=c[0]}}\n\n[Color0Intense]\nColor={{=c[8]}}\n\n[Color1]\nColor={{=c[1]}}\n\n[Color1Intense]\nColor={{=c[9]}}\n\n[Color2]\nColor={{=c[2]}}\n\n[Color2Intense]\nColor={{=c[10]}}\n\n[Color3]\nColor={{=c[3]}}\n\n[Color3Intense]\nColor={{=c[11]}}\n\n[Color4]\nColor={{=c[4]}}\n\n[Color4Intense]\nColor={{=c[12]}}\n\n[Color5]\nColor={{=c[5]}}\n\n[Color5Intense]\nColor={{=c[13]}}\n\n[Color6]\nColor={{=c[6]}}\n\n[Color6Intense]\nColor={{=c[14]}}\n\n[Color7]\nColor={{=c[7]}}\n\n[Color7Intense]\nColor={{=c[15]}}\n\n# --- general options ---\n\n[General]\nDescription=terminal.sexy\nOpacity=1\nWallpaper=\n";

module.exports = {
  export: createExporter(template, _.partialRight(_.mapValues, function (color) {
    return color.toRgbArray().join();
  }))
};
