'use strict';

var _ = require('lodash');

var createExporter = require('../exporter');

var template = "BackgroundColour={{=c.background}}\nForegroundColour={{=c.foreground}}\nCursorColour={{=c.foreground}}\nBlack={{=c[0]}}\nBoldBlack={{=c[8]}}\nRed={{=c[1]}}\nBoldRed={{=c[9]}}\nGreen={{=c[2]}}\nBoldGreen={{=c[10]}}\nYellow={{=c[3]}}\nBoldYellow={{=c[11]}}\nBlue={{=c[4]}}\nBoldBlue={{=c[12]}}\nMagenta={{=c[5]}}\nBoldMagenta={{=c[13]}}\nCyan={{=c[6]}}\nBoldCyan={{=c[14]}}\nWhite={{=c[7]}}\nBoldWhite={{=c[15]}}\n";

module.exports = {

  export: createExporter(template, _.partialRight(_.mapValues, function (color) {
    return color.toRgbArray().join();
  }))

};
