'use strict';

var _ = require('lodash');

var createExporter = require('../exporter');

var template = "[Configuration]\nColorCursor={{=c.foreground}}\nColorForeground={{=c.foreground}}\nColorBackground={{=c.background}}\nColorPalette={{=c[0]}};{{=c[1]}};{{=c[2]}};{{=c[3]}};{{=c[4]}};{{=c[5]}};{{=c[6]}};{{=c[7]}};{{=c[8]}};{{=c[9]}};{{=c[10]}};{{=c[11]}};{{=c[12]}};{{=c[13]}};{{=c[14]}};{{=c[15]}}\n";

module.exports = {
  export: createExporter(template, _.partialRight(_.mapValues, function (color) {
    return color.toDoubleHex();
  }))
};
