'use strict';

var _ = require('lodash');

var createExporter = require('../exporter');

var template = "[terminal.sexy]\ntext={{=c.foreground}}\ntext(bold)={{=c.foreground}}\nbackground={{=c.background}}\nblack={{=c[0]}}\nblack(bold)={{=c[8]}}\nred={{=c[1]}}\nred(bold)={{=c[9]}}\ngreen={{=c[2]}}\ngreen(bold)={{=c[10]}}\nyellow={{=c[3]}}\nyellow(bold)={{=c[11]}}\nblue={{=c[4]}}\nblue(bold)={{=c[12]}}\nmagenta={{=c[5]}}\nmagenta(bold)={{=c[13]}}\ncyan={{=c[6]}}\ncyan(bold)={{=c[14]}}\nwhite={{=c[7]}}\nwhite(bold)={{=c[15]}}\n[Names]\nname0=terminal.sexy\ncount=1\n";

module.exports = {
    export: createExporter(template, _.partialRight(_.mapValues, function (color) {
        return color.toHex().substring(1);
    }))
};
